import React, {useState, useEffect} from 'react'

export default function Tag (props) {
    const [tagName, setTag] = useState('');

    useEffect(() => {
        if(props) {
            setTag(props.data)
            }
    }, [props])
    let name = tagName.trim();
    if (name.length > 0){ 
        return (
            <a href="" className="tag-pill tag-default">{name}</a> 
        );
    }
return <a href="" className="tag-pill tag-default">tag</a>;
};
