import React, {useState} from 'react'
import InputField from '../../components/InputField'
import GenericButton from '../../components/Button';
import api from '../../providers/api'

export default function Login ({data}) {

    const [user, setUser] = useState();
    const [pass, setPass] = useState();
    const [authUser, setAuthUser] = useState();

    function sendData () {
        // const authUser = {criar objeto pra passar dados de login}
        console.log(u);
        api.post(`/users/login`, `{"user":{"email":${user}, "password":"${pass}}}`)
        .then(response => {
             setAuthUser({response})
             console.log(response)
         })
         .catch(error => {
            setAuthUser({error: true});
     });

    }

    return (<>
        <InputField type="text" label="Usuario" onChange={setUser}/>
        <InputField type="password" label="Senha" onChange={setPass}/>
        <GenericButton type="submit" label="Entrar" data={sendData}/>
        </>

    )
}
