import React from "react";

export default function ActionButton (props) {

  if(props.action == 'favorite'){
    console.log('é fav');
  }else{
    console.log('é follow');
  }

  return (
      <button onClick={props.slug} className={props.class} ><i className={props.iconClass}></i>&nbsp; {props.label} {props.content} <span class="counter">({props.counter})</span></button>
  );
};
