import React, {useState} from 'react'
import InputField from '../../components/InputField'
import GenericButton from '../../components/Button';
import api from '../../providers/api'
import swal from 'sweetalert';
import { BrowserRouter as Router, Switch, Route, Link, Redirect, useHistory, useLocation } from "react-router-dom";
import Header from '../../components/Header';


export default function Register () {
    let history = useHistory();

    const [username, setUser] = useState();
    const [password, setPass] = useState();
    const [email, setEmail] = useState();
    const [registerUser, setRegisterUser] = useState();

    function sendData () {
        console.log('chamou');

        const registerUser = {
            user: {
                username: username, 
                email: email, 
                password: password
            }
        }

        api.post(`/users`, registerUser)
        .then(response => {
            setRegisterUser({response})
            localStorage.setItem('token', response.data.token)
            console.log('rep', response.data.token);
            swal("Registered Successfully");
            history.push('/login');
        })
         .catch(error => {
            swal(error.message);
            console.log('chamou');
        });
    }

    return (<>
        <Header/>
        <div class="auth-page">
            <div class="container page">
                <div class="row">

                <div class="col-md-6 offset-md-3 col-xs-12">
                    <h1 class="text-xs-center">Sign up</h1>
                    <p class="text-xs-center">
                    <a href="">Have an account?</a>
                    </p>

                    <ul class="error-messages">
                    <li>That email is already taken</li>
                    </ul>

                    <form>
                    <fieldset class="form-group">
                        <input class="form-control form-control-lg" type="text" placeholder="Your Name"/>
                    </fieldset>
                    <fieldset class="form-group">
                        <input class="form-control form-control-lg" type="text" placeholder="Email"/>
                    </fieldset>
                    <fieldset class="form-group">
                        <input class="form-control form-control-lg" type="password" placeholder="Password"/>
                    </fieldset>
                    <button class="btn btn-lg btn-primary pull-xs-right">
                        Sign up
                    </button>
                    </form>
                </div>

                </div>
            </div>
        </div>
        <InputField type="text" label="User" onChange={setUser}/>
        <InputField type="text" label="Email" onChange={setEmail}/>
        <InputField type="password" label="Password" onChange={setPass}/>
        <GenericButton type="submit" label="Register" data={sendData}/>
        </>
    );
}
