import React from "react";
import Moment from 'moment';
import RedirectLink from '../../components/Link';

export default function Card (props) {
  let article = props.data;
  let formattedDate = article.createdAt;

  let pathUser = '/profiles/' + article.author.username

  console.log('card', props.data);
  return (
    <div class="article-preview">
      <div class="article-meta">
        <a href="/profile"><img src={article.author.image} /></a>
        <div class="info">
          <RedirectLink  path={pathUser} primaryClass="author" label={article.author.username}/>
          <span class="date">{Moment(formattedDate).format('MMM D')}</span>
        </div>
        <button class="btn btn-outline-primary btn-sm pull-xs-right">
          <i class="ion-heart"></i> {article.favoritedCount}
        </button>
      </div>
      <a href="" class="preview-link">
        <h1>{article.title}</h1>
        <p>{article.description}</p>
        <RedirectLink path="/register" label="Read more..."/>
      </a>
    </div>
  );
};
