// import React, {useState} from 'react'
import InputField from '../../components/InputField'
import GenericButton from '../../components/Button';
import Label from '../../components/Label';
import api from '../../providers/api'
import swal from 'sweetalert';
import React, { useContext, createContext, useState } from "react";
import { BrowserRouter as Router, Switch, Route, Link, Redirect, useHistory, useLocation } from "react-router-dom";
import Header from '../../components/Header';

export default function Login ({data}) {
    let history = useHistory();

    const [user, setEmail] = useState();
    const [pass, setPass] = useState();
    const [authUser, setAuthUser] = useState();

    function sendData () {
        const authUser = {
            user: {
                email: user, 
                password: pass
            }
        }
        api.post(`/users/login`, authUser)
        .then(response => {
            setAuthUser({response});
            localStorage.setItem('token', response.data.user.token);
            history.push('/settings');
         })
         .catch(error => {
             console.log(error.message)
            swal(error.message);
        });
    }

    return (<>
      <Header/>
        <div class="auth-page">
            <div class="container page">
                <div class="row">

                <div class="col-md-6 offset-md-3 col-xs-12">
                    <h1 class="text-xs-center">Sign up</h1>
                    <p class="text-xs-center">
                        <a href="">Have an account?</a>
                    </p>
                    <ul class="error-messages">
                        <li>That email is already taken</li>
                    </ul>
                    <form>
                        <fieldset class="form-group">
                            <InputField class="form-control form-control-lg" type="text" onChange={setEmail} placeholder="Your Eamil"/>
                        </fieldset>
                        <fieldset class="form-group">
                            <InputField class="form-control form-control-lg" type="password" placeholder="Password" onChange={setPass}/>
                        </fieldset>
                        {/* <GenericButton type="submit" label="Login" data={sendData}/> */}

                        <button class="btn btn-lg btn-primary pull-xs-right">
                            Sign up
                        </button>
                    </form>
                </div>

                </div>
            </div>
  {/* <>
           <Label label="Or"/>
           <Link to="/register">teste</Link>
           <GenericButton type="button" label="Sign Up" data={sendData}/>
       </> */}
</div>
</>

    )
}
