import React, {useState} from 'react'
import InputField from '../../components/InputField'
import GenericButton from '../../components/Button';
import api from '../../providers/api'
import swal from 'sweetalert';
import { BrowserRouter as Router, Switch, Route, Link, Redirect, useHistory, useLocation } from "react-router-dom";
import TextField from '../../components/TextField';

export default function Register () {
    let history = useHistory();

    const [username, setUser] = useState();
    const [password, setPass] = useState();
    const [email, setEmail] = useState();
    const [registerUser, setRegisterUser] = useState();

    function sendData () {
        console.log('chamou');

        const registerUser = {
            user: {
                username: username, 
                email: email, 
                password: password
            }
        }

        api.post(`/users`, registerUser)
        .then(response => {
            setRegisterUser({response})
            localStorage.setItem('token', response.data.token)
            console.log('rep', response.data.token);
            swal("Registered Successfully").then( () => {
                history.push('/login');
            });
        })
         .catch(error => {
            swal(error.message);
        });
    }

    return (<>
        <div class="editor-page">
          <div class="container page">
            <div class="row">

              <div class="col-md-10 offset-md-1 col-xs-12">
                <form>
                  <fieldset>
                    <fieldset class="form-group">
                        <InputField class="form-control form-control-lg" type="text" placeholder="Article Title"/>
                    </fieldset>
                    <fieldset class="form-group">
                        <input type="text" class="form-control" placeholder="What's this article about?"/>
                    </fieldset>
                    <fieldset class="form-group">
                        <TextField class="form-control form-control-lg" rows="8" placeholder="Short bio about you"  onChange={setBio}/>
                        <textarea class="form-control" rows="8" placeholder="Write your article (in markdown)"></textarea>
                    </fieldset>
                    <fieldset class="form-group">
                        <input type="text" class="form-control" placeholder="Enter tags"/><div class="tag-list"></div>
                    </fieldset>
                    <button class="btn btn-lg pull-xs-right btn-primary" type="button">
                        Publish Article
                    </button>
                  </fieldset>
                </form>
              </div>

            </div>
          </div>
        </div>
        </>
    );
}
